import "../css/Game.css";

// React
import { useState } from "react";

// Components
import Tetris from "./Tetris/Tetris";
import RankingPage from "./Rank";



const Game = () => {
    const [isStart, setIsStart] = useState(false);
    const onClickSingle = () => {
        if(isStart === false){
            setIsStart(true);
        }else{
            setIsStart(false);
        }
    }
    const [isRank, setIsRank] = useState(false);
    const onClickRank = () => {
        if(isRank === false){
            setIsRank(true);
        }else{
            setIsRank(false);
        }
    }
    const element = document.querySelector('#Tetris');
    let blurValue = 30;
    let increasing = false;

    function animateBlur() {
        if (blurValue === 30) {
        increasing = false;
        } else if (blurValue === 20) {
        increasing = true;
        }

        if (increasing) {
        blurValue += 1;
        } else {
        blurValue -= 1;
        }

        element.style.textShadow = `0 0 ${blurValue}px rgb(177, 36, 36)`;

        setTimeout(animateBlur, 0);
    }
    animateBlur();

    return(
        <div className="Game">
            {isStart ? <Tetris setIsStart ={onClickSingle}/> : 
            isRank ? <RankingPage setIsRank={onClickRank}/> :
            (<div id="Home">
                <h1 id="Tetris">TETRIS</h1>
                <div className="button-list">
                    <button onClick={onClickSingle}>Single Play</button>
                    <button onClick={onClickRank}>Ranking</button>
                    <button>Setting</button>
                </div>
            </div>)}
        </div>
    )
}
export default Game;
