import { useEffect, useState } from "react";
import "../css/TetrisModal.css";
import Game from "./Game";
import Loading from "./Loading";


const TetrisModal = () => {
    const [isLoading, setIsLoading] = useState(true);

    // 테트리스 켜는 로밍화면 구현
    // FIXME default 3000
    useEffect( () => {
        setTimeout( () => {
            setIsLoading(false);
        }, 0)
    })

    return(
        <div id = "tetris-modal" className="tetris-modal">
            {isLoading ? <Loading /> : <Game />}
        </div>
    );
}

export default TetrisModal;
