import { useState, useEffect } from "react";
import { useCountDown } from "../hooks/useCountDown";
import Board from "./Board";
import "../css/Tetris.css";


const Tetris = () => {
    const [isClick, setIsClick] = useState(false);
    const [isStarted, setIsStarted] = useState(false); //3초 카운트 다운
    const countDown = useCountDown(isClick, setIsStarted);
    const onClick = () => setIsClick(true);

    useEffect(()=> {}, [isStarted])
    return(
        <div className="Tetris">
            {isClick && countDown > 0 && <span className="count-down">{countDown}</span>}
            <Board isStarted={isStarted} />
            {isClick ? null : <button onClick={onClick} className="start-button">Start</button>}
        </div>
    );
}

export default Tetris;
