import { useEffect, useState } from "react";
import Cell from "./Cell";
import "../css/Board.css";
import { useSelector } from "react-redux";
import Preview from "./Preview";
import GameStats from "./GameStats";
import { useQueue } from "../hooks/useQueue";



const Board = () => {
    const initGrid = Array.from({ length: 20 }, () => new Array(10).fill(0));
    const [grid, setGrid] = useState(initGrid);
    const isStart = useSelector((store) => store.isStart.value);
    //아래의 level, line, score는 redux를 사용하는 편이 좋아보임
    const [level, setLevel] = useState(1);
    const [line, setLine] = useState(0);
    const [score, setScore] = useState(0);


    //queue를 pop()할 때 복사한 값으로 pop()하고 그 남은 값을 setBlockQueue에 전달해 setter가 재 할당 가능하게 함.
    //block queue state
    const [blockQueue, setBlockQueu, pop] = useQueue();
    //grid current Block(shape)
    // const [block, setBlock] = useState([]);
    //Preview props(shape)
    // const [previewShape, setPreviewShape] = useState([]);
    // if(!isStart) return; //count down check
    let shape; //현재 조종중인 블럭
    const previewShape = blockQueue[blockQueue.length-1];
    useEffect( () => {
        shape = pop();
    }, shape)





    return(
        <div className="board">
            <h1 className="board-title-tetris">TETRIS</h1>

            <div className="grid">
                {grid.map((row, rowIndex) => row.map((col, colIndex) => {
                    return <Cell 
                        key={`${rowIndex}, ${colIndex}`}
                        color={grid[rowIndex][colIndex] === 1 ? 'white' : 'black'}
                    />
                }))}
                <Preview shape={previewShape}/>
            </div>


            <GameStats 
                level={level}
                line={line}
                score={score}
            />
        </div>
    );
}
export default Board;
