import { styled } from "styled-components";

import { useEffect, useRef } from "react";

import PreviewCell from "./PreviewCell";
import { TETROMINOS } from "../../constants";


const Preview = ({queue}) => {
    const initArr = Array.from({ length: 4 }, () => new Array(4).fill(0));
    const shapeRef = useRef(0);

    useEffect(() => {
        if(queue.isEmpty()) {
            queue.init();
        }
        shapeRef.current = queue.items[queue.items.length-1];
    }, [queue, queue.items.length]);


    console.log(TETROMINOS[shapeRef.current].shape);


    return (
        <StyledPreview>
            {initArr.map((row, y) =>
            row.map((cell, x) =>
                TETROMINOS[shapeRef.current].shape[x][y] !== 0 ?
                <PreviewCell key={x} color="red" /> : 
                <PreviewCell key={x} color={TETROMINOS[shapeRef.current]?.color} />
            ))}
        </StyledPreview>
    );
}

export default Preview;

const StyledPreview = styled.div`
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: repeat(4, 1fr);
    box-sizing: content-box;
    background-color: transparent;
    width: 100px;
    height: 100px;
    border: 2px solid #111;

    div {
        display: inline;
        width: auto;

        border: 1px solid aliceblue;
    }
`

