// SelectionBox.js
import React, { useEffect, useRef} from 'react';
import useSelectionBox from '../hooks/useSelectionBox';

const SelectionBox = () => {
  const {
    selectionStyle,
    mouseDown,
    handleMouseDown,
    handleMouseUp,
    handleMouseMove,
  } = useSelectionBox();

  const WrapperStyle = {
    position: 'absolute',
    width: '100%',
    height: '100%',
    userSelect: 'none',
  };

  return (
    <div
      id="wrapper-id"
      style={WrapperStyle}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
      onMouseMove={handleMouseMove}
    >
      {mouseDown && <div style={selectionStyle} />}
    </div>
  );
};

export default SelectionBox;
