import { useState } from 'react';

import { randomTetromino } from '../../constants';

export const usePlayer = () => {
    const [player, setPlayer] = useState({
        pos:  {x: 0, y: 0 },
        tetromino: randomTetromino().shape,
        collide: false,
    });

    const updatePlyerPos = ({ x, y, collide }) => {
        setPlayer(prev => ({
            ...prev,
            pos: { x: (prev.pos.x += x), y: (prev.pos.y += y)},
            collide
        }))
    }

    const resetPlayer = () => {

    }
    return [player, updatePlyerPos, resetPlayer];
}
