import React, { useEffect, useRef } from "react";



function Rain() {
  const canvasRef = useRef(null);
  const tetriminosRef = useRef([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    class Tetrimino {
        constructor() {
            this.x = Math.random() * canvas.width;
            this.y = -20;
            this.speed = Math.random() * 2 + 2;
            this.color = this.randomColor();
            this.blocks = [];
            this.createTetrimino();
            this.rotateAngle = 0;
            this.rotateSpeed = Math.random() * 0.02; 
            this.rotateDirection = Math.round(Math.random()) * 2 - 1;
            this.startDelay = Math.floor(Math.random() * 200); 
        }
    }
    createTetriminos();
    animate();

    function createTetriminos() {
      for (let i = 0; i < 100; i++) {
        tetriminosRef.current.push(new Tetrimino());
      }
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      for (const tetrimino of tetriminosRef.current) {
        tetrimino.draw();
      }

      requestAnimationFrame(animate);
    }
  }, []);

  return <canvas ref={canvasRef} />;
}

export default Rain;
