import { useState, useEffect } from "react";

export const useCountDown = (isClick, setIsStarted) => {
    const [count, setCount] = useState(3);

    useEffect( () => {
        let timer;
        if (isClick && count > 0) {
            timer = setInterval(() => {
            setCount((prevCount) => prevCount - 1);
            }, 1000);
        }
        return () => {
            setIsStarted(true);
            clearInterval(timer);
        };
    }, [isClick, count, setIsStarted]);

    return count;
};
