import "../../css/GameOver.css"
import {useEffect, useRef, useState} from "react"
import axios from 'axios';

// {
//   name: 사용자가 입력한 이름,
//   score: 사용자 점수
// }




const GameOver = ({ score, restart, setIsStart }) => {
    const inputRef = useRef(null);
    const [inputValue, setInputValue] = useState('');

    axios.post('/api/data', { name: {inputValue}, score: {score} })
    .then(response => {
        // 요청에 대한 응답 처리
        console.log(response.data);
    })
    .catch(error => {
        // 오류 처리
        console.error(error);
    });

    useEffect(() => {
      if (inputRef.current) {
        inputRef.current.focus();
      }
    }, []);

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
    }

    return (
      <div className="gameover-modal">
        <h1>Game Over</h1>
        <input ref={inputRef} onChange={handleInputChange} value={inputValue} maxLength="9"/>
        <span>
          score : <span>{score}</span>
        </span>
        <div className="gameover-modal-buttons">
          <button onClick={restart}>Play Again</button>
          <button onClick={setIsStart}>Main Menu</button>
          <button>Quit</button>
        </div>
      </div>
    );
  };

export default GameOver
