import { useEffect } from "react";

export const useGetBlock = ({shape, grid, setGrid}) => {
    const position = {
        row: 0,
        col: (10 / 2)
    }
    const newGrid = [...grid];
    for(let row=0; row<shape.length; row++) {
        for(let col=0; col<shape[row].length; col++) {
            if(shape[row][col] === 1) {
                const x = position.row + row;
                const y = position.col + col;
                newGrid[x][y] = 1;
            }
        }
    }
    useEffect( () => {
        setGrid(newGrid);
    }, [newGrid, setGrid])
}
