// useSelectionBox.js
import { useEffect, useState } from 'react';

const useSelectionBox = () => {
  const [mouseDown, setMouseDown] = useState(false);
  const [selection, setSelection] = useState({ x1: 0, y1: 0, x2: 0, y2: 0 });

  const handleMouseDown = (e) => {
    setMouseDown(true);
    setSelection({ ...selection, x1: e.clientX, y1: e.clientY });

  };

  const handleMouseUp = () => {
    setMouseDown(false);
    setSelection({ x1: 0, y1: 0, x2: 0, y2: 0 });
  };

  const handleMouseMove = (e) => {
    if (!mouseDown) return;
    setSelection({ ...selection, x2: e.clientX, y2: e.clientY });
  };

  const selectionStyle = {
    position: 'fixed',
    left: Math.min(selection.x1, selection.x2),
    top: Math.min(selection.y1, selection.y2),
    width: Math.abs(selection.x1 - selection.x2),
    height: Math.abs(selection.y1 - selection.y2),
    border: '1px solid blue',
    backgroundColor: 'rgba(0, 0, 255, 0.2)',
    pointerEvents: 'none',
    overflow: 'visible'

  };

  return {
    selectionStyle,
    mouseDown,
    handleMouseDown,
    handleMouseUp,
    handleMouseMove,
  };
};

export default useSelectionBox;
