import "../css/Game.css";
import Help from "./Help";
import Tetris from "./Tetris/Tetris";
import RankingPage from "./Rank";
import { useState } from "react";


const Game = () => {
    const [isStart, setIsStart] = useState(false);
    const onClickSingle = () => {
        if(isStart === false){
            setIsStart(true);
        }else{
            setIsStart(false);
        }
    }
    const [isRank, setIsRank] = useState(false);
    const onClickRank = () => {
        if(isRank === false){
            setIsRank(true);
        }else{
            setIsRank(false);
        }
    }

    return(
        <div className="Game">
            {isRank ? <RankingPage setIsRank={onClickRank}/> :
            (<div id="tetris">
                <h1>TETRIS</h1>
                <Help />
                <div className="button-list">
                    <button onClick={onClickSingle}><span>{'>'}</span>Single Play</button>
                    <button onClick={onClickRank}><span>{'>'}</span>Ranking</button>
                    <button><span>{'>'}</span>Setting</button>
                </div>
            </div>)}
        </div>
    )
}
export default Game;
