import "../css/Game.css";
import useDragger from "../hooks/useDragger";
import Help from "./Help";
import Tetris from "./Tetris";
import TetrisHeader from "./TetrisHeader";
import { useState } from "react";

// TODO Button 호버시 > 생기게 css 작업하기
const Game = () => {
    const [isStart, setIsStart] = useState(false);
    const onClickSingle = () => {
        setIsStart(true);
    }

    return(
        <div className="Game">
            <TetrisHeader />
            {isStart ? <Tetris /> : 
            (<>
            <div id="tetris">
                <h1>TETRIS</h1>
            </div>
            <div className="tetris-contents">
                <Help />
                <div className="button-list">
                    <button onClick={onClickSingle}>Single Play</button>
                    <button>Multi Play</button>
                    <button>Setting</button>
                </div>
            </div>
            </>)}
        </div>
    )
}
export default Game;
