import { useState, useEffect } from "react";

export const useCountDown = () => {
    const [countDown, setCountDown] = useState(3);

    useEffect( () => {
        setInterval( () => {
            setCountDown((prev) => prev -1);
        }, 1000); 
    }, [])

    return countDown;
}
