import { useDispatch } from "react-redux";
import "../css/TetrisHeader.css";
import { setIsTetris } from "../store/IsTetris";
import useDragger from "../hooks/useDragger";

const TetrisHeader = () => {
    useDragger("tetris-modal");
    const dispatch = useDispatch();
    const onClickQuit = () => {
        dispatch(setIsTetris(false));
    }

    return (
        <div id="moveTarget1" className="Header">
            <span id="moveTarget2">Tetris</span>
            <div className="tetris-control">
                <span>_</span>
                <span>â–¡</span>
                <span className="X" onClick={onClickQuit}>X</span>
            </div>
        </div>
    );
}
export default TetrisHeader;
