import { useState, useEffect } from "react";
import Cell from "./Cell";
import "../css/Board.css";
import Preview from "./Preview";
import GameStats from "./GameStats";
import { useCountDown } from "../hooks/useCountDown";
import { useSelector } from "react-redux";
import { useShapeAndColor } from "../hooks/useShapeAndColor";




const Board = () => {
    const isStart = useSelector((store) => store.isStart.value);
    const [isClick, setIsClick] = useState(false);
    const countDown = useCountDown(isClick);
    const onClick = () => setIsClick(true);
    const initGrid = Array.from({ length: 20 }, () => new Array(10).fill(0));
    const [grid, setGrid] = useState(initGrid);

    //아래의 level, line, score는 redux를 사용하는 편이 좋아보임
    const [level, setLevel] = useState(1);
