    return(
        <div className="board">

            <h1 className="board-title-tetris">TETRIS</h1>
            {isClick && countDown > 0 && <span className="count-down">{countDown}</span>}
            <div className="grid">
                {grid.map((row, rowIndex) => row.map((col, colIndex) => {
                    return <Cell 
                        key={`${rowIndex}, ${colIndex}`}
                        color={grid[rowIndex][colIndex] === 1 ? color : 'black'}
                    />
                }))}
                {!isClick && <button onClick={onClick} className="start-button">START</button>}
