import { useState, useRef, useEffect } from "react";
import Cell from "./Cell";
import "../css/Board.css";

import Preview from "./Preview";
import GameStats from "./GameStats";
import { useQueue } from "../hooks/useQueue";
import { useGetBlock } from "../hooks/useGetBlock";



const Board = () => {
    const initGrid = Array.from({ length: 20 }, () => new Array(10).fill(0));
    const [grid, setGrid] = useState(initGrid);

    // if(!isStart) return; //count down check
    //아래의 level, line, score는 redux를 사용하는 편이 좋아보임
    const [level, setLevel] = useState(1);
    const [line, setLine] = useState(0);
    const [score, setScore] = useState(0);


    let shape = useRef(null); //현재 조종중인 블럭
    const previewShape = useRef(null); //다음 블럭
    const [blockQueue, setBlockQueue, pop] = useQueue();
    const [newGrid, color] = useGetBlock(shape, grid, setGrid);



    return(
        <div className="board">

            <h1 className="board-title-tetris">TETRIS</h1>

            <div className="grid">
                {grid.map((row, rowIndex) => row.map((col, colIndex) => {
                    return <Cell 
                        key={`${rowIndex}, ${colIndex}`}
                        color={grid[rowIndex][colIndex] === 1 ? color : 'black'}
                    />
                }))}
                <Preview shape={previewShape.current}/>
            </div>


            <GameStats 
                level={level}
                line={line}
                score={score}
            />

        </div>
    );
}
export default Board;
