const Cell = ({rowIndex, colIndex, grid}) => {
    const data = grid[rowIndex][colIndex];
    const color = data === 0 ? 'transparent' : 'white';
    const styledCell = {
        'width': '30px',
        'height': '30px',
        'font-size': '10px',
        'background-color': color,
        'border': '1px solid var(--color-gray)',
    }

    return(
        <div style={styledCell}></div>
    );
}

export default Cell;
