import { useState } from "react"
import { selectColor } from "../utill.js/selectColor";
import { initQueue } from "../utill.js/initQueue";

export const useShapeAndColor = () => {
    const [blockQueue, setBlockQueue] = useState(initQueue());
    const [shape, setShape] = useState(blockQueue.pop()); //현재 조종중인 블럭
    const [color, setColor] = useState(selectColor(shape));
    const [previewShape, setPreviewShape] = useState(blockQueue[blockQueue.length-1]); //다음 블럭
    const pop = () => {
        if(blockQueue.length === 1) {
            const x = blockQueue.pop();
            setBlockQueue(initQueue());
            return x;
        } else {
            return blockQueue.pop();
        }
    }
    return [shape, previewShape, color, pop];
}
