import { styled } from "styled-components";
import { StyledTetrisWrapper } from "../../css/StyledTetris";

import { createStage } from "../../gameHelpers";

// Components
import Stage from "./Stage";
import Display from "./Display";
import StartButton from "./StartButton";

const Tetris = () => {

    return(
        <StyledTetrisWrapper>
            <StyledTetris>
                <Stage stage={ createStage() } />
                <aside>
                    <div>
                        <Display text="Score" />
                        <Display text="Rows" />
                        <Display text="Level" />
                    </div>
                    <StartButton />
                </aside>
            </StyledTetris>
        </StyledTetrisWrapper>
    );
}

export default Tetris;
