import { useEffect, useState } from "react";
import Cell from "./Cell";
import { selectColor } from "../utill.js/selectColor";

const Preview = ({shape}) => {
    const initGrid = Array.from({ length: 4 }, () => new Array(4).fill(0));
    const [grid, setGrid] = useState(initGrid);
    const color = selectColor(shape);

    useEffect( () => {
        // let temp = [...shape];
        // for (let i = 0; i < shape.length; i++) {
        //     for (let j = 0; j < shape[i].length; j++) {
        //         if (shape[i][j] === 1) {
        //             temp[i][j] = 1;
        //         }
        //     }
        // }
        // setGrid(temp);
        const temp = [...grid];
        temp[0][2] = 1;
        temp[2][2] = 1;
        setGrid(temp)
    }, [shape])

    return(
        <div className="preview">
            {grid.map((row, rowIndex) => row.map((col, colIndex) => {
                return <Cell 
                    key={`${rowIndex}, ${colIndex}`}
                    color={grid[rowIndex][colIndex] === 1 ? color : 'black'}
                    rowIndex={rowIndex}
                    colIndex={colIndex}
                />
            }))}
        </div>
    );
}
export default Preview;
