import React from 'react';

import styled from 'styled-components';
import { TETROMINOS } from '../../constants';


const Cell = ({ type, isGhost }) => {

    return(
        <StyledCell type={type} color={TETROMINOS[type].color} ghost={isGhost ? 'true' : 'false'} >
        </StyledCell>
    );
}

export default Cell;
// export default React.memo(Cell);

const StyledCell = styled.div`
    display: inline;
    width: auto;
    background-color: ${props => props.ghost ? 'red' : 'blue'};

    border: ${props => (props.type === 0 ? '0px solid' : '2px solid')};
    border-bottom-color: rgba(${props => props.color}, 0.1);
    border-right-color: rgba(${props => props.color}, 1);
    border-top-color: rgba(${props => props.color}, 1);
    border-left-color: rgba(${props => props.color}, 0.3);

    /* border: rgba(${props => props.ghost ? '2px solid white' : 'none'}); */
    border-radius: 4px;
`
