import { useState, useEffect } from "react";

export const useCountDown = () => {
    const [countDown, setCountDown] = useState(3);

    useEffect( () => {
        const interval = setInterval( () => {
            setCountDown((prev) => prev -1);
        }, 1000); 
        if(countDown < 0) {
            return () => {
                clearInterval(interval);
            }
        }
    }, [])

    return countDown;
}
