import { useState, useEffect } from "react";
import Cell from "./Cell";
import "../css/Board.css";
import Preview from "./Preview";
import GameStats from "./GameStats";
import { initQueue } from "../utill.js/initQueue";
import { useSelector } from "react-redux";
import { useShapeAndColor } from "../hooks/useShapeAndColor";




const Board = () => {
    const isStart = useSelector((store) => store.isStart.value);

    const initGrid = Array.from({ length: 20 }, () => new Array(10).fill(0));
    const [grid, setGrid] = useState(initGrid);

    //아래의 level, line, score는 redux를 사용하는 편이 좋아보임
    const [level, setLevel] = useState(1);
    const [line, setLine] = useState(0);
    const [score, setScore] = useState(0);

    useEffect( () => {
        if(!isStart) return;        
    }, [])
    const [shape, previewShape, color, pop] = useShapeAndColor();









    return(
        <div className="board">

            <h1 className="board-title-tetris">TETRIS</h1>

            <div className="grid">
                {grid.map((row, rowIndex) => row.map((col, colIndex) => {
                    return <Cell 
                        key={`${rowIndex}, ${colIndex}`}
                        color={grid[rowIndex][colIndex] === 1 ? color : 'black'}
                    />
                }))}
                <Preview shape={previewShape}/>
            </div>


            <GameStats 
                level={level}
                line={line}
                score={score}
            />

        </div>
    );
}
export default Board;
