import Footer from "./Footer";
import "../css/Desktop.css";
import tetris from "../img/tetris.png";
import pc from "../img/pc.png";
import trash from "../img/trash.png";

import TetrisModal from "./TetrisModal";
import { useDispatch, useSelector } from "react-redux";
import { setIsTetris } from "../store/IsTetris";


const Desktop = () => {
    const dispatch = useDispatch();
    const isTetris = useSelector((store) => store.isTetris.value);
    const onClickTetris = () => dispatch(setIsTetris(true));

    return(
        <>
        <div className="desktop">

            <div className="desktop-icons">
                {/* TRASH */}
                <button className="desktop-button">
                    {/* <img src={trash} alt="trash" /> */}
                    <span>휴지통</span>
                </button>

                {/* MY PC */}
                <button className="desktop-button">
                    {/* <img src={myPC} alt="myPC" /> */}
                    <span className="my-pc-text">내 컴퓨터</span>
                </button>

                {/* TETRIS */}
                <button className="desktop-button" onClick={onClickTetris}>
                    <img src={tetris} alt="TETRIS-img" className="desktop-tetris" />
                    <span>Tetris</span>
                </button>
            </div>

            {/* Tetris modal control */}
            {
                isTetris ? <TetrisModal /> : <></>
            }
        </div>
        <Footer />
        </>
    )
}

export default Desktop;
