import { useEffect, useState, useRef } from "react"
import { selectColor } from "../utill.js/selectColor";
import { initQueue } from "../utill.js/initQueue";

export const useShapeAndColor = () => {
    const [blockQueue, setBlockQueue] = useState(initQueue());

    const shapeRef = useRef(null);
    const colorRef = useRef(null);

    const previewShapeRef = useRef(null);
    const preivewColorRef = useRef(null);


    const pop = () => {
        if(blockQueue.length === 1) {
            const x = blockQueue.pop();
            setBlockQueue(initQueue());
            return x;
        } else {
            return blockQueue.pop();
        }
    }
    useEffect( () => {
        shapeRef.current = blockQueue.pop();
        colorRef.current = selectColor(shapeRef.current);

        previewShapeRef.current = blockQueue[blockQueue.length-1];
        preivewColorRef.current = selectColor(previewShapeRef.current);
    }, [blockQueue])
    return {
        shape: shapeRef.current,
        previewShape: previewShapeRef.current,
        color: colorRef.current,
        previewColor: preivewColorRef.current,
        pop
    };
}
