export const STAGE_WIDTH = 10;
export const STAGE_HEIGHT = 20;

export const createStage = () => {
    return Array.from(Array(STAGE_HEIGHT), () => new Array(STAGE_WIDTH).fill([0, 'clear']));
}

export const checkCollision = (player, stage, { x: moveX, y: moveY}) => {
    for(let y = 0; y < player.tetromino.length; y++) {
        for(let x = 0; x < player.tetromino[y].length; x++) {
            // 2중 체크 (보드안에서만, 다른 블록과의 충돌)
            if(player.tetromino[y][x] !== 0) {

                const nextY = stage[y + player.pos.y + moveY];
                const nextX = stage[y + player.pos.y + moveY][x + player.pos.x + moveX];
                const collide = stage[y + player.pos.y + moveY][x + player.pos.x + moveX][1] !== 'clear';
                if(!nextY || !nextX || !collide) return true;
            }
        }
    }
}
